import {React,useState} from 'react'
import logo from "../../images/logo.png"

const SelfRepo = () => {
    const [userData, setUserData]=useState([]);
    const [repoNames, setRepoNames] = useState([]);
    async function getUserData(){
        await fetch("http://localhost:5000/getUserData",{
            method: "GET",
            headers: {
                "Authorization" :"Bearer " +localStorage.getItem("accessToken")
            }
        }).then((response)=>{
            return response.json();
        }).then((data)=>{
            console.log(data);
            setUserData(data)
            return fetch(data.repos_url, {
                method: "GET",
                headers: {
                    "Authorization": "Bearer " + localStorage.getItem("accessToken")
                }
            });
        })
        .then(response => {
            return response.json();
        })
        .then(repos => {
            console.log(repos); // Just for debugging, remove this line

            // Extract repository names
            const names = repos.map(repo => repo.name);
            setRepoNames(names); // Set the repository names in the state
        })
        .catch(error => {
            console.error("Error:", error);
        });

    }

  return (
    <div>
      <div>
        <img src={logo} alt=''/>
      </div>
      <div>
        <h1>Welcome Arya Soni !</h1>
        <p>Github Repository List</p>
      </div>
      <div>
      <button onClick={getUserData}>Get data</button>
        <textarea>
{
    <>
    <h3>Repositories:</h3>
    <ul>
            {repoNames.map(repoName => (
                <li key={repoName}>{repoName}</li>
            ))}
        </ul>
    </>
}
        </textarea>
      </div>
    </div>
  )
}

export default SelfRepo
