import React,{useState} from 'react';
import "./welcome.css";
import logo from "../../images/logo.png"

const Welcome = () => {
    const [activeButton, setActiveButton] = useState(null);

    const handleClick = (buttonName) => {
        setActiveButton(buttonName === activeButton ? null : buttonName);
    };
  return (
    <div className='container-welcome'>

      <div className='div-img'>
        <img src={logo} className="logo" alt='logo'/>
      </div>
      <div className='heading'>
        <h1>Welcome Arya Soni !</h1>
        <p className='para'>Choice From The Following</p>
      </div>
      <div className='div-btn'>
        <button className={`btn ${isDeveloperActive ? 'active' : ''}`}
        onClick={handleDeveloperClick}
        >
        Developer
        </button>


        <button className={`btn ${isOrganisationActive ? 'active' : ''}`}
                    onClick={handleOrganisationClick}
                >Organisation</button>


        <button className={`btn ${isCompanyActive ? 'active' : ''}`}
                    onClick={handleCompanyClick}
                >Company</button>
      </div>
    </div>
  )
}

export default Welcome
